sql.trim:

	AND a.title like '%${title}%'


	AND a.status = #{status}


	AND a.class_id = #{class_id}

AND a.is_deleted =0

mapper.select:
SELECT a.sid,a.title,a.brief,a.content,a.status,a.uri,a.meta_keyword,a.meta_desc,a.class_id,a.created_by,a.created_at,a.modified_by,a.modified_at,a.is_deleted, b.last_name,b.first_name,b.email FROM content a left join user b on a.created_by=b.user_id
AND a.sid=#{sid}
AND a.uri = #{uri}
AND a.is_deleted =0

mapper.update:
UPDATE content set
is_deleted=1
WHERE sid=#{sid}
